int j=0;
int doit=0;
int nodob=0,nodobt=1;
int trig=0;
double mpn=Math.PI/180;
String m="";
void numbers(int u)    {
      if (deci==1){
    i=-i;
        disp[j]=disp[j]+ (double) (u*Math.pow(10.0000, i)*100)/100;
        }
 else
     disp[j]=(disp[j]*10)+u;
    print();
if (deci==1)
    i=-i;
i++;
nodob=1;

}
