void trigono(String k, int tt)
    {
     if (nodobt == 0)
    return;
    if (disp[j]==0)
    {
       jTextField1.setText(k);
                disp[j]=1;
            }
        else
    jTextField1.setText(disp[j]+k);
        doit=tt;
        nodobt=0;
        m=k;
        print();
        j++;
        trig=1;
    }
